package tipeData;

public class BilanganBulat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		byte tipeByte   = 50;
		short tipeShort = 0x7e23;
		int tipeInt     = 21466666;
		long tipeLong   = 922000000;
		// Cetak
		System.out.println("Bilangan Bulat");
		System.out.println("--------------");
		System.out.println("Byte  = " +tipeByte);
		System.out.println("Short = " +tipeShort);
		System.out.println("Int   = " +tipeInt);
		System.out.println("Long  = " +tipeLong);
	}

}
