package tipeData;

public class BilanganReal {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		float tipeFloat1 = 0.13f;
		float tipeFloat2 = .25f;
		// Deklarasi double
		double tipeDouble1 = 0.4e-42;
		double tipeDouble2 = 432.12D;
		// Cetak
		System.out.println("Bilangan Real - Desimal");
		System.out.println("-----------------------");
		System.out.println("Float1  = " +tipeFloat1);
		System.out.println("Float2  = " +tipeFloat2);
		System.out.println("Double1 = " +tipeDouble1);
		System.out.println("Double2 = " +tipeDouble2);
	}

}
